parameters.query:
create table chats_anh (
  id bigserial primary key,
  created_at TIMESTAMPTZ, 
  phone text,
  updated_at text
);

parameters.jsCode:
let telefoneRaw;
let telefoneCorreto;

// 1. Tenta pegar do formato processado (por ex. depois de um Set)
if ($json.Telefone) {
  telefoneRaw = $json.Telefone;
  telefoneCorreto = telefoneRaw.endsWith('@lid') && $json.body?.data?.key?.senderPn
    ? $json.body.data.key.senderPn
    : telefoneRaw;
}

// 2. Tenta pegar do JSON bruto vindo direto do webhook
else if ($json.body?.data?.key?.remoteJid) {
  telefoneRaw = $json.body.data.key.remoteJid;
  telefoneCorreto = telefoneRaw.endsWith('@lid') && $json.body?.data?.key?.senderPn
    ? $json.body.data.key.senderPn
    : telefoneRaw;
}

// 3. Caso não encontre nada, define como null
else {
  telefoneCorreto = null;
}

return [
  {
    json: {
      telefoneCorreto
    }
  }
];
