// n8n Code Node - Run Once for All Items

const items = $input.all();

// Pega o primeiro item da entrada
let root = items[0]?.json ?? {};

// Caso a resposta venha como array na raiz (igual ao exemplo),
// pega o primeiro elemento desse array
if (Array.isArray(root)) {
  root = root[0] ?? {};
}

// A lista de neg√≥cios vem em root.data
const inputData = root.data ?? [];

const results = inputData.map((business) => {
  const lead = business.lead || {};
  const stage = business.stage || {};
  const pipeline = stage.pipeline || {};
  const attendant = business.attendant || {};

  // üìû Telefone: usa phone ou primeiro contactId
  let telefone = "";
  if (lead.phone) {
    telefone = lead.phone;
  } else if (Array.isArray(lead.contacts) && lead.contacts.length > 0) {
    telefone = lead.contacts[0]?.contactId || "";
  }

  // ‚úâÔ∏è Email
  const email = lead.email || "";

  // üîß Indexa additionalFields por NOME
  const adicionaisPorNome = {};
  if (Array.isArray(business.additionalFields)) {
    for (const af of business.additionalFields) {
      const nome = af.additionalField?.name;
      if (!nome) continue;
      adicionaisPorNome[nome] = af;
    }
  }

  // üßæ Campos importantes vindos de additionalFields
  const cpf        = adicionaisPorNome["CPF"]?.value        || "";
  const ra         = adicionaisPorNome["RA"]?.value         || "";
  const polo       = adicionaisPorNome["Polo"]?.value       || "";

  // ‚úÖ PTC vindo do additionalFields
  const ptc        = adicionaisPorNome["PTC"]?.value        || "";

  // ‚úÖ Troca: "Modalidade" agora vem do campo "N√≠vel"
  const modalidade = adicionaisPorNome["N√≠vel"]?.value      || "";

  const curso      = adicionaisPorNome["Curso"]?.value      || "";
  const nivel      = adicionaisPorNome["N√≠vel"]?.value      || "";

  // üìÖ Matr√≠cula (campo "Matr√≠cula" ‚Üí valueDate ou value) formatada DD/MM/YYYY (GMT-3)
  let matriculaFormatada = "";
  const matriculaRaw =
    adicionaisPorNome["Matr√≠cula"]?.valueDate ||
    adicionaisPorNome["Matr√≠cula"]?.value ||
    "";

  if (matriculaRaw) {
    const d = new Date(matriculaRaw);
    if (!isNaN(d.getTime())) {
      const brasiliaDate = new Date(d.getTime() - 3 * 60 * 60 * 1000);
      const pad = (n) => n.toString().padStart(2, "0");
      matriculaFormatada =
        `${pad(brasiliaDate.getDate())}/${pad(
          brasiliaDate.getMonth() + 1
        )}/${brasiliaDate.getFullYear()}`;
    }
  }

  // üìÖ createdAt do neg√≥cio formatado DD/MM/YYYY (GMT-3)
  let createdAtFormatado = "";
  if (business.createdAt) {
    const d = new Date(business.createdAt);
    if (!isNaN(d.getTime())) {
      const brasiliaDate = new Date(d.getTime() - 3 * 60 * 60 * 1000);
      const pad = (n) => n.toString().padStart(2, "0");
      createdAtFormatado =
        `${pad(brasiliaDate.getDate())}/${pad(
          brasiliaDate.getMonth() + 1
        )}/${brasiliaDate.getFullYear()}`;
    }
  }

  // üè∑Ô∏è Tags (somente os nomes)
  const tags = Array.isArray(lead.tags)
    ? lead.tags.map((t) => t.name)
    : [];

  // üîÅ Mapa de campos adicionais completo (pra consulta futura)
  const camposAdicionais = {};
  if (Array.isArray(business.additionalFields)) {
    for (const af of business.additionalFields) {
      const nomeCampo = af.additionalField?.name || af.id;
      if (!nomeCampo) continue;
      camposAdicionais[nomeCampo] = {
        value: af.value ?? null,
        valueNumber: af.valueNumber ?? null,
        valueDate: af.valueDate ?? null,
        raw: af,
      };
    }
  }

  // Objeto bruto completo do neg√≥cio
  const campos = business;

  return {
    json: {
      // üîê DADOS SENS√çVEIS PRIMEIRO
      name: lead.name ?? "",
      telefone,
      cpf,
      ra,
      matricula: matriculaFormatada,
      email,

      // üéì Dados acad√™micos
      polo,

      // ‚úÖ adiciona PTC na sa√≠da
      ptc,

      // ‚úÖ modalidade agora vem de "N√≠vel"
      modalidade,

      curso,
      nivel,

      // üìõ IDs e nomes principais
      business_id: business.id ?? "",
      lead_id: lead.id ?? business.leadId ?? "",
      stage_id: stage.id ?? business.stageId ?? "",
      pipeline_id: pipeline.id ?? "",
      attendant_id: attendant.id ?? business.attendantId ?? "",

      stage_nome: stage.name ?? "",
      pipeline_nome: pipeline.name ?? "",
      attendant_nome: attendant.name ?? "",

      // üè∑Ô∏è Tags e datas
      tags,
      status: business.status ?? "",
      code: business.code ?? null,
      createdAt: business.createdAt ?? "",
      createdAtFormatado,
      lastMovedAt: business.lastMovedAt ?? "",

      // Campos auxiliares
      camposAdicionais,
      campos, // objeto bruto completo
    },
  };
});

return results;

const leads = $json._embedded?.leads || [];

if (leads.length === 0) {
  return [{ json: { quantidade_encontrada: 0, id_mais_recente: "" } }];
}

// Ordena por ID (maior = mais recente)
const leadsOrdenados = leads
  .filter(lead => typeof lead.id === "number")
  .sort((a, b) => b.id - a.id);

const quantidade_encontrada = leads.length;
const id_mais_recente = leadsOrdenados[0]?.id ?? "";

// Retorno final
return [{
  json: {
    quantidade_encontrada,
    id_mais_recente
  }
}];

