// Intervalo com bordas: [hora - anteMin , hora + duracaoMin + posMin]
function emIntervaloComBordas(horaHHMMSS, anteMin, duracaoMin, posMin, refDateSP){
  if(!horaHHMMSS || !(refDateSP instanceof Date)) return false;
  const [H, M=0, S=0] = String(horaHHMMSS).split(':').map(Number);
  if(!Number.isFinite(H)) return false;

  const base = new Date(refDateSP);
  base.setHours(H, M, S, 0);

  const ini = new Date(base.getTime() - (Number(anteMin)||0)*60*1000);
  const fim = new Date(base.getTime() + ((Number(duracaoMin)||0)+(Number(posMin)||0))*60*1000);

  return refDateSP >= ini && refDateSP <= fim;
}

