  const pausaFim    = emIntervaloComBordas(saidaHora,  saidaAnteMin, /*duracao*/0, /*pos*/0, referenciaAgora);

  // disponibilidade por status + pausas
  const disponivelStatus = (ativo && !naoDistribui && okAlmoco && okExpediente && !pausaAlmoco && !pausaFim);

