// ===================== Normaliza√ß√£o de datas =====================
// Aceita: ISO (ex: 2025-07-19 08:55:08), "dd/MM/yy - HH:mm" ou "dd/MM/yy HH:mm"
function parseDateFlexible(input) {
  if (!input) return null;
  if (input instanceof Date) return isNaN(input.getTime()) ? null : input;

  const s = String(input).trim();

  // Tenta Date direto (ISO ou parecido)
  const d1 = new Date(s.replace(' - ', ' ')); // ajuda em "dd/MM/yy - HH:mm"
  if (!isNaN(d1.getTime())) return d1;

  // Tenta "dd/MM/yy - HH:mm" ou "dd/MM/yyyy - HH:mm"
  const m = s.match(/^(\d{2})\/(\d{2})\/(\d{2,4})\s*-\s*(\d{2}):(\d{2})$/)
           || s.match(/^(\d{2})\/(\d{2})\/(\d{2,4})\s+(\d{2}):(\d{2})$/);
  if (m) {
    let [_, dd, MM, yy, HH, mm] = m;
    let yyyy = yy.length === 2 ? (Number(yy) < 70 ? 2000 + Number(yy) : 1900 + Number(yy)) : Number(yy);
    const month = Number(MM) - 1;
    const dt = new Date(yyyy, month, Number(dd), Number(HH), Number(mm), 0);
    return isNaN(dt.getTime()) ? null : dt;
  }
  return null;
}
function toMs(input) {
  const d = parseDateFlexible(input);
  return d ? d.getTime() : 0;
}

