  else if (pausaAlmoco)     { status = 'INDISPONIVEL'; motivo = 'PAUSA_ALMOCO_INTERVALO'; }
  else if (pausaFim)        { status = 'INDISPONIVEL'; motivo = 'PAUSA_SAIDA_INTERVALO'; }

