};

// Hora atual (segundos desde 00:00:00) em America/Sao_Paulo
const nowSPSeconds = (() => {
  const fmt = new Intl.DateTimeFormat('pt-BR', {
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false, timeZone: 'America/Sao_Paulo'
  });
  const parts = Object.fromEntries(fmt.formatToParts(new Date()).map(p => [p.type, p.value]));
  const h = Number(parts.hour || 0);
  const m = Number(parts.minute || 0);
  const s = Number(parts.second || 0);
  return h * 3600 + m * 60 + s;
})();

const parseHMS = (hhmmss) => {
  if (typeof hhmmss !== 'string') return null;
  const m = hhmmss.trim().match(/^(\d{1,2}):(\d{2}):(\d{2})$/);
  if (!m) return null;
  const h = Number(m[1]), mi = Number(m[2]), s = Number(m[3]);
  if (h > 23 || mi > 59 || s > 59) return null;
  return h * 3600 + mi * 60 + s;
};

const fmtHMS = (sec) => {
  if (sec == null) return null;
  let s = Math.max(0, Math.floor(sec)) % 86400;
  const h = String(Math.floor(s / 3600)).padStart(2, '0');
  s %= 3600;
  const m = String(Math.floor(s / 60)).padStart(2, '0');
  const ss = String(s % 60).padStart(2, '0');
  return `${h}:${m}:${ss}`;
};

