  // 1) Ultimo Consultor (1029785) -> 2) Consultor (294046)
  let nomeConsultor = getCF(lead, 1029785) || getCF(lead, 294046);

  let resolvedId = null;

  // Resolver pelo nome do consultor na Distribui√ß√£o 1
  if (nomeConsultor) {
