  const ativo_inativo            = r.ativo_inativo           ?? r?.json?.ativo_inativo;
  const volume_distribuicao      = r.volume_distribuicao     ?? r?.json?.volume_distribuicao;

  // Fila: 1) usa a do HTTP se existir por id; 2) sen√£o usa a contagem de leads (HTTP Kommo)
  const filaHTTPLinha = filaByIdHTTP.get(id);
  const fila = (filaHTTPLinha !== undefined) ? filaHTTPLinha : (counts[id] || 0);

  const pausa_distribuicao_min   = r.pausa_distribuicao      ?? r?.json?.pausa_distribuicao ?? 0;

  // pass-through
  const timestamp                = r.timestamp               ?? r?.json?.timestamp ?? null;
  const ultima_execucao          = r.ultima_execucao         ?? r?.json?.ultima_execucao ?? null;
  const status_almoco            = r.status_almoco           ?? r?.json?.status_almoco ?? null;
  const status_final_expediente  = r.status_final_expediente ?? r?.json?.status_final_expediente ?? null;
  const status_final             = r.status_final            ?? r?.json?.status_final ?? null;

  // === Almo√ßo ===
  const almoco_base = r.almoco_real ?? r?.json?.almoco_real ?? r.almoco ?? r?.json?.almoco;
  const lunchStartSec = parseHMS(almoco_base);
  const lunchEndSec   = (lunchStartSec != null) ? (lunchStartSec + LUNCH_MINUTES * 60) : null;
  const lunchEndNorm  = (lunchEndSec != null) ? (lunchEndSec % 86400) : null;

  let em_almoco = false;
  if (lunchStartSec != null && lunchEndSec != null) {
    if (lunchEndSec >= lunchStartSec) {
      em_almoco = nowSPSeconds >= lunchStartSec && nowSPSeconds < lunchEndSec;
    } else {
      em_almoco = (nowSPSeconds >= lunchStartSec) || (nowSPSeconds < lunchEndNorm);
    }
  }

  const almoco_inicio = (lunchStartSec != null) ? fmtHMS(lunchStartSec) : null;
  const almoco_fim    = (lunchEndSec   != null) ? fmtHMS(lunchEndSec)   : null;

  // === Final do expediente ===
  const expediente_base = r.final_expediente ?? r?.json?.final_expediente;
  const expSec = parseHMS(expediente_base);

  let em_expediente = true;
  if (expSec != null) {
    em_expediente = nowSPSeconds < expSec;
  }

  const expediente_fim = (expSec != null) ? fmtHMS(expSec) : null;

  // === Pausa Distribui√ß√£o ===
  const pausaSegundos = (Number(pausa_distribuicao_min) || 0) * 60;

  return {
    json: {
      id,
      nome,
      total_leads: counts[id] || 0,
      ativo_inativo,
      volume_distribuicao,
      fila, // << agora vem do HTTP (linha) ou da contagem de leads do HTTP
      pausa_distribuicao_min: Number(pausa_distribuicao_min) || 0,
      pausa_distribuicao_fmt: fmtHMS(pausaSegundos),

      // pass-through
      timestamp,
      ultima_execucao,
      status_almoco,
      status_final_expediente,
      status_final,

      // almo√ßo
      em_almoco,
      almoco_inicio,
      almoco_fim,
      status_almoco_calculado: em_almoco ? 'Em Almo√ßo' : 'Fora do Almo√ßo',

      // expediente
      em_expediente,
      expediente_fim,
      status_expediente_calculado: em_expediente ? 'Em Expediente' : 'Fora do Expediente',
