// Aceita:
// 1) items = [ { json: { ...evento } }, ... ]
// 2) items = [ { json: [ { ...evento }, ... ] } ]

function extractDriveIds(text) {
  const ids = new Set();
  if (!text) return ids;
  const s = String(text);

  // Padr√µes mais comuns do Drive (arquivo)
  const patterns = [
    /https?:\/\/drive\.google\.com\/file\/d\/([A-Za-z0-9_-]{10,})/g,
    /https?:\/\/drive\.google\.com\/open\?id=([A-Za-z0-9_-]{10,})/g,
    /https?:\/\/drive\.google\.com\/uc\?(?:[^#\s]*&)?id=([A-Za-z0-9_-]{10,})/g,
    // se voc√™ tamb√©m quiser aceitar links compartilhados encurtados do "id=":
    /[?&]id=([A-Za-z0-9_-]{10,})/g,
  ];

  let m;
  for (const re of patterns) {
    while ((m = re.exec(s)) !== null) {
      ids.add(m[1]);
    }
  }

  return ids;
}

// Normaliza para uma lista de eventos
const events = Array.isArray(items[0]?.json)
  ? items[0].json
  : items.map(i => i.json);

const allIds = new Set();

for (const ev of events) {
  // VARRE SOMENTE description e summary (onde est√£o seus links do Drive)
  const parts = [];
  if (ev?.description) parts.push(String(ev.description));
  if (ev?.summary)     parts.push(String(ev.summary));

  const blob = parts.join(' ');
  const ids = extractDriveIds(blob);
  ids.forEach(id => allIds.add(id));
}

// Sa√≠da: um item por arquivo + item de contagem
const out = [];
for (const id of allIds) {
  out.push({
    json: {
      tipo: 'documento',
      driveFileId: id,
      url: `https://drive.google.com/uc?export=download&id=${id}`
    }
  });
}

out.push({
  json: {
    tipo: 'contagem',
    totalIds: out.length // j√° √© a quantidade de documentos (√© calculado antes de adicionar este item)
  }
});

return out;

// junta todos os PHOTO_ID em attached_media
const attached = items
  .map(i => i.json.id)
  .filter(Boolean)
  .map(id => ({ media_fbid: id }));

return [{
  json: {
    attached_media: attached
  }
}];

// Pega o evento
const event = $json;

// Summary vem do Calendar Trigger
const msg = $('Google Calendar Trigger').first().json.summary || '';

// Cria o corpo inicial
const body = { message: msg };

// Conta imagens
const attached = event.attached_media || [];
attached.forEach((m, i) => {
  body[`attached_media[${i}]`] = JSON.stringify({ media_fbid: m.media_fbid });
});

// Adiciona a quantidade total
body.total_images = attached.length;

return [{ json: body }];

