const row = items[0].json;

const keys = Object.keys(row).filter(
  k => k.toLowerCase().includes('id da') && k.toLowerCase().includes('imagem')
);

function extractId(urlOrId) {
  if (!urlOrId) return null;
  const m1 = String(urlOrId).match(/\/file\/d\/([^/]+)/);
  if (m1) return m1[1];
  const m2 = String(urlOrId).match(/[?&]id=([^&]+)/);
  if (m2) return m2[1];
  if (/^[A-Za-z0-9_-]{20,}$/.test(String(urlOrId))) return String(urlOrId);
  return null;
}

const out = [];
for (const k of keys) {
  const id = extractId(row[k]);
  if (id) {
    out.push({
      json: {
        tipo: 'imagem',   // <-- ADICIONADO
        driveFileId: id,
        url: `https://drive.google.com/uc?export=download&id=${id}`
      }
    });
  }
}

// item de contagem
out.push({
  json: {
    tipo: 'contagem',    // <-- ADICIONADO
    totalIds: out.length
  }
});

return out;

// junta todos os PHOTO_ID em attached_media
const attached = items
  .map(i => i.json.id)
  .filter(Boolean)
  .map(id => ({ media_fbid: id }));

return [{
  json: {
    attached_media: attached
  }
}];

// Entrada: { attached_media: [ { media_fbid: "111" }, { media_fbid: "222" }, ... ] }

const msg = $items('Get row(s) in sheet')[0].json['Texto da Imagem'] || '';

const body = { message: msg };

// Transformar o array em campos attached_media[0], attached_media[1], ...
(items[0].json.attached_media || []).forEach((m, i) => {
  // o Graph exige o valor como JSON string
  body[`attached_media[${i}]`] = JSON.stringify({ media_fbid: m.media_fbid });
});

return [{ json: body }];
